  // ===== 2. HELPER FUNCTIONS =====

  // Fix empty or # links
  function fixEmptyLink(href) {
    if (!href || href.trim() === "#" || href.trim() === "") {
      return "/coming-soon.html";
    }
    return href;
  }

  // Recursively create nav items (supports multi-level dropdowns)
  function createNavItem(link) {
    const li = document.createElement("li");
    const a = document.createElement("a");
    a.textContent = link.text;
    a.href = fixEmptyLink(link.href);

    // ===== ACTIVE LINK LOGIC =====
    const linkPath = a.getAttribute("href").replace(window.location.origin, "");
    if (linkPath !== "#" && !linkPath.startsWith("tel:") && !linkPath.startsWith("mailto:")) {
      if (window.location.pathname === linkPath) {
        a.classList.add("active");
      }
    }

    li.appendChild(a);

    // Handle subLinks (dropdowns)
    if (link.subLinks && link.subLinks.length > 0) {
      li.classList.add("dropdown");

      const ul = document.createElement("ul");
      ul.classList.add("dropdown-menu");

      link.subLinks.forEach(sub => {
        const subLi = createNavItem(sub); // recursive call
        ul.appendChild(subLi);
      });

      li.appendChild(ul);

      // Mobile toggle (click)
      a.addEventListener("click", (e) => {
        if (window.innerWidth < 992) {
          e.preventDefault();
          ul.classList.toggle("show");
        }
      });

      // Desktop hover
      li.addEventListener("mouseenter", () => {
        if (window.innerWidth >= 992) ul.classList.add("show");
      });
      li.addEventListener("mouseleave", () => {
        if (window.innerWidth >= 992) ul.classList.remove("show");
      });
    }

    return li;
  }

  // ===== 3. POPULATE HEADER =====
  function populateHeader() {
    // LOGO
    const logoLink = document.getElementById("logoLink");
    if (headerData.logo.svg) {
      logoLink.innerHTML = headerData.logo.svg; // Insert inline SVG
    }
    logoLink.href = fixEmptyLink(headerData.logo.link);

    // NAVIGATION
    const navLinksContainer = document.getElementById("navLinks");
    navLinksContainer.innerHTML = "";
    headerData.navLinks.forEach(link => {
      navLinksContainer.appendChild(createNavItem(link));
    });

    // CTA – PRIMARY
    const ctaPrimary = document.getElementById("ctaPrimary");
    if (headerData.cta.primary.show) {
      ctaPrimary.href = fixEmptyLink(headerData.cta.primary.href);
      ctaPrimary.innerHTML = `
        ${headerData.cta.primary.text}
        <span class="cta-icon d-flex align-items-center justify-content-center">
          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="24" viewBox="0 0 25 24" fill="none">
            <path fill-rule="evenodd" clip-rule="evenodd"
              d="M22.9111 10.3484C19.0375 10.3484 15.507 6.68003 15.507 2.65165V1H12.3292V2.65165C12.3292 5.58168 13.5654 8.33002 15.5054 10.3484H0.5V13.6517H15.5054C13.5654 15.67 12.3292 18.4183 12.3292 21.3484V23H15.507V21.3484C15.507 17.32 19.0375 13.6517 22.9111 13.6517H24.5V10.3484H22.9111Z"
              fill="white" />
          </svg>
        </span>
      `;
      ctaPrimary.classList.remove("d-none");
    }

    // CTA – PHONE
    const ctaPhone = document.getElementById("ctaPhone");
    ctaPhone.href = fixEmptyLink(headerData.cta.phone.href);
    ctaPhone.innerHTML = `
      ${headerData.cta.phone.text}
      <span class="cta-icon d-flex align-items-center justify-content-center d-none">
        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="24" viewBox="0 0 25 24" fill="none">
          <path fill-rule="evenodd" clip-rule="evenodd"
            d="M22.9111 10.3484C19.0375 10.3484 15.507 6.68003 15.507 2.65165V1H12.3292V2.65165C12.3292 5.58168 13.5654 8.33002 15.5054 10.3484H0.5V13.6517H15.5054C13.5654 15.67 12.3292 18.4183 12.3292 21.3484V23H15.507V21.3484C15.507 17.32 19.0375 13.6517 22.9111 13.6517H24.5V10.3484H22.9111Z"
            fill="white" />
        </svg>
      </span>
    `;
  }

  // ===== 4. MOBILE NAV TOGGLE =====
  const mobileToggle = document.querySelector(".mobile-nav-toggle");
  if (mobileToggle) {
    mobileToggle.addEventListener("click", () => {
      document.getElementById("navmenu").classList.toggle("open");
      mobileToggle.classList.toggle("bi-list");
      mobileToggle.classList.toggle("bi-x");
    });
  }

  // ===== 5. INIT =====
  populateHeader();

